ALTER TABLE dbo.OrgSchedule ADD [NextMeetingDate]  AS (CONVERT([DATETIME],DATEADD(DAY,CASE WHEN ([SchedDay]-(DATEPART(WEEKDAY,GETDATE())-(1)))<(0) THEN ((7)+[SchedDay])-(DATEPART(WEEKDAY,GETDATE())-(1)) WHEN ([SchedDay]-(DATEPART(WEEKDAY,GETDATE())-(1)))=(0) THEN (0) ELSE [SchedDay]-(DATEPART(WEEKDAY,GETDATE())-(1)) END,CONVERT([DATE],GETDATE())))+CONVERT([DATETIME],CONVERT([TIME],[MeetingTime])))
GO
